const MobileView = styled.div`
  .profile-title {
    font-weight: 500;
    font-size: 19px;
    color: #000000;
    margin-left: 50px;
  }
  ${mobileViewMin({
    display: "none",
  })}
`;

const DesktopView = styled.div`
  background: #f3f3f3;
  height: 700px;
  ${mobileViewMax({
    display: "none",
  })}
`;

const OverflowContainer = styled.div`
  height: 420px;
  width: 100%;

  .interest-item {
    height: 45px;
    width: 140px;
    border-radius: 15px;
    background: #fff;
    padding: 9px;
    cursor: pointer;
    border: 1px solid #e8e6ea;
  }

  .interest-icon {
    height: 19px;
    width: 19px;

    img {
      height: 100%;
      width: 100%;
      object-fit: contain;
    }
  }

  .interest-text {
    font-weight: 400;
    font-size: 14px;
    line-height: 150%;
    color: #3b3a3c;
    margin-left: 9px;
  }

  .active {
    background-color: #b50d0d;
  }

  .active-text {
    color: white;
  }
`;

const Details = styled.div`
  height: 100%;
  overflow-y: auto;
  &::-webkit-scrollbar {
    width: 6px;
    margin-block: 20px;
  }
  &::-webkit-scrollbar-track {
    background: #ffe7e7;
    border-radius: 20px;
  }
  &::-webkit-scrollbar-thumb {
    background: #b50d0d;
    opacity: 0.5;
    border-radius: 20px;
  }
`;

